import React, { useEffect, useState } from "react";
import styled from "styled-components";
import axios from "axios";
import { CryptoState } from "../../CryptoContext/CryptoContext";
import AliceCarousel from "react-alice-carousel";
import { Link } from "react-router-dom";
import "./Carousel.css";
import { TrendingCoins } from "../../Config/api";

function Carousel() {
  const { symbol, currency } = CryptoState();
  const [trending, setTrending] = useState([]);

  const fetchTrendingCoins = async (currency) => {
    const { data } = await axios.get(TrendingCoins(currency));
    setTrending(data);
  };

  useEffect(() => {
    fetchTrendingCoins(currency);
  }, [currency]);

  const myStyle={
fontSize:"48px",
fontWeight:800,
  }

  console.log(trending);
  const items = trending.map((coin) => {
    let price24 = coin?.price_change_percentage_24h;

    return (
      <Link className="coin_card" to={`/coins/${coin.id}`}>
      
        <img src={coin?.image} alt={coin.name}></img>
        <div style={{marginTop:"20px"}}>
        <h2>{coin.name}</h2>
        {price24 >= 0 ? (
          <h2 style={{ color: "green" }}>{price24}</h2>
        ) : (
          <h2 style={{ color: "red" }}>{price24}</h2>
        )}
        <p style={myStyle}>{`${symbol} ${coin.current_price}`}</p>
      </div>
        
      </Link>
    );
  });
  const responsive = {
    0: {
      items: 2,
    },
    512: {
      items: 4,
    },
  };

  return (
    <ContainerCorousel>
      <AliceCarousel
        mouseTracking
        infinite
        autoPlayInterval={1000}
        animationDuration={1500}
        disableButtonsControls
        responsive={responsive}
        autoPlay
        items={items}
        disableDotsControls
        className="alice_carousel"
      />
    </ContainerCorousel>
  );
}

export default Carousel;

const ContainerCorousel = styled.div`
  height: 50%;
  display: flex;
  align-items: currency;
  justify-content: center;
  flex-direction: row;
  margin: 40px;
`;
