import React, { useEffect, useState } from "react";
import styled from "styled-components";
import axios from "axios";
import { CryptoState } from "../../CryptoContext/CryptoContext";
import AliceCarousel from "react-alice-carousel";
import { Link } from "react-router-dom";
import './Carousel.css'

function Carousel() {
  const { symbol, currency } = CryptoState();
  const [trending, setTrending] = useState([]);

  const fetchTrendingCoins = async () => {
    const promise = new Promise((res) => {
      setTimeout(() => {
        res(
          axios.get(
            `https://api.coingecko.com/api/v3/coins/markets?vs_currency=${currency}&order=gecko_desc&per_page=10&page=1&sparkline=false&price_change_percentage=24h`
          )
        );
      }, 100);
    });
    const { data } = await promise;
    setTrending(data);
  };

  useEffect(() => {
    fetchTrendingCoins();
  }, [currency]);
  console.log(trending);
  const items = trending.map((coin) => {
    let price = coin.price_change_percentage_24h;
    
    return (
      <Link className="coin_card" to={`/coins/${coin.id}`}>
        <img src={coin.image} alt="image"></img>
        <h2>{coin.name}</h2>
        <h3>{`${coin.current_price} ${symbol}`}</h3>
        <h3>{price}</h3>
      </Link>
    );
  });
  const responsive = {
    0: {
      items: 2,
    },
    767: {
      items: 4,
    },
  };

  return (
    <ContainerCorousel>
      <AliceCarousel
        mouseTracking
        infinite
        autoPlayInterval={1000}
        animationDuration={1500}
        disableButtonsControls
        responsive={responsive}
        autoPlay
        items={items}
        className="alice_carousel"
        disableDotsControls
      />
    </ContainerCorousel>
  );
}

export default Carousel;

const ContainerCorousel = styled.div`
  height: 50%;
  display: flex;
  align-items: center;
  flex-direction: row;
  margin: 20px;
`;
