import { SingleCoin } from "../../Config/api";
import axios from "axios";
import { useContext, useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { CryptoState } from "../../CryptoContext/CryptoContext";
import styled from "@emotion/styled";

const Detail = () => {
  const { id } = useParams();
  const [coinToDisplay, setCoinToDisplay] = useState([]);
  const { currency, symbol } = useContext(CryptoState);
    const [isLoading,setIsloading]= useState(false);
    
  const coinDataFetch = async () => {
    const { data } = await new Promise((res) => {
      setTimeout(() => {
        try {
          res(axios.get(SingleCoin(id)));
        } catch (error) {
          console.error(error);
        }
      }, 1);
    });
    setCoinToDisplay(data);
  };
  console.log(coinToDisplay);
  useEffect(() => {
    coinDataFetch();
  }, []);

  return (
    <div className="main_container">
      <DetailSection>
      </DetailSection>
      <ChartSection></ChartSection>
    </div>
  );
};
export default Detail;

const DetailSection = styled.div`
  width: 30%;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
`;

const ChartSection = styled.div`
  width: 70%;
`;
