import { useContext, useEffect, useState } from "react";
import { CryptoState } from "../../CryptoContext/CryptoContext";
import { CoinData } from "../../Config/api";
import axios from "axios";
import './CoinList.css'
import { Container, LinearProgress, Table, TableCell, TableContainer, TableHead, TableRow } from "@mui/material";

const CoinList = () => {
  const { currency, symbol } = useContext(CryptoState);
  const [coinSummary, setCoinSummary] = useState([]);
  const [loading, setLoading] = useState(false);

  const fetchCoinData = async (currency) => {
    const { data } = await new Promise((res, rej) => {
      setTimeout(() => {
        try {
          res(axios.get(CoinData(currency)));
        } catch (err) {
          console.log(err);
        }
      }, 10);
    });
    setCoinSummary(data);
  };
  console.log(coinSummary);
  useEffect(() => {
    setLoading(true);
    fetchCoinData(currency);
    setLoading(false);
  }, [currency]);
  return <div >
  <Container className="coin_summary_contaner">

 
     <h1>Get detail of your coins with market cap</h1>
     <input type="search" style={{width:"80%"}} placeholder="Seach for your coin"/>
     <TableContainer>
         {
             loading?<LinearProgress style={{backgroundColor:"gold"}}></LinearProgress>
             :
             <Table>
                 <TableHead style={{backgroundColor:"#EEBC1D"}}>
<TableRow>
    {["Coin","Price","24Hrs-Change","Market Cap"].map(element=>{
        return(
        <TableCell
        style={{
            color:"black",
            fontWeight:"700",
            fontFamily:"Montserrat"
        }}
        key={element}
        >
        {element}

        </TableCell>
        )
    })}
</TableRow>
                 </TableHead>
             </Table>
         }
     </TableContainer>
     </Container>
  </div>;
};
export default CoinList;
