        <div style={{marginTop:"20px",display:"flex",flexDirection:"column",justifyContent:"center",alignItems:"center"}}>
        <h2>{coin.name}</h2>
        {price24 >= 0 ? (
          <h2 style={{ color: "green" }}>{price24}</h2>
        ) : (
          <h2 style={{ color: "red" }}>{price24}</h2>
