import { SingleCoin } from "../../Config/api";
import axios from "axios";
import { useContext, useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { CryptoState } from "../../CryptoContext/CryptoContext";
import styled from "@emotion/styled";

const Detail = () => {
  const { id } = useParams();
  const [coinToDisplay, setCoinToDisplay] = useState([]);
  const { currency, symbol } = useContext(CryptoState);

  const coinDataFetch = async () => {
    const { data } = await new Promise((res) => {
      setTimeout(() => {
        try {
          res(axios.get(SingleCoin(id)));
        } catch (error) {
          console.error(error);
        }
      }, 10);
    });
    setCoinToDisplay(data);
  };
  console.log(coinToDisplay);
  useEffect(() => {
    coinDataFetch();
  }, []);

  return (
    <MainContainer>
      <DetailSection>
      <img src={coinToDisplay.image.large}/>
      <span>{coinToDisplay.symbol}</span>
      <span>{coinToDisplay.name}</span>
      </DetailSection>
      <ChartSection></ChartSection>
    </MainContainer>
  );
};
export default Detail;

const MainContainer= styled.div`
display:flex;
align-items:center;
justify-content:center;
padding:20px;
`

 const DetailSection = styled.div`
 width:30%;
 `

 const ChartSection = styled.div`
 width:70%;
 `
