import React, { useEffect, useState } from "react";
import styled from "styled-components";
import axios from "axios";
import { CryptoState } from "../../CryptoContext/CryptoContext";
import AliceCarousel from "react-alice-carousel";
import { Link } from "react-router-dom";
import "./Carousel.css";
import { TrendingCoins } from "../../Config/api";
import "react-alice-carousel/lib/alice-carousel.css";

function Carousel() {
  const { symbol, currency } = CryptoState();
  const [trending, setTrending] = useState([]);

  const fetchTrendingCoins = async (currency) => {
    const { data } = await axios.get(TrendingCoins(currency));
    setTrending(data);
  };
  console.log(trending);

  useEffect(() => {
    fetchTrendingCoins(currency);
  }, [currency]);

  const myStyle = {
    fontSize: "48px",
    fontWeight: 800,
  };

  const items = trending.map((coin) => {
    let price24 = coin?.price_change_percentage_24h;

    return (
      <Link className="coin_card" to={`/coins/${coin?.id}`}>
        <img src={coin?.image} alt={coin?.name}/>
        <div
          style={{
            marginTop: "20px",
            display: "flex",
            flexDirection: "column",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <h2>{coin?.name}</h2>
          {price24 >= 0 ? (
            <h2 style={{ color: "green" }}>{`+${price24} %`}</h2>
          ) : (
            <h2 style={{ color: "red" }}>{`-${price24} %`}</h2>
          )}
          <p style={myStyle}>{`${symbol} ${coin?.current_price.toFixed(2)}`}</p>
        </div>
      </Link>
    );
  });
  const responsive = {
    0: {
      items: 2,
    },
  };
  return (
    <ContainerCorousel>
      <AliceCarousel
        mouseTracking
        infinite
        autoPlayInterval={1000}
        animationDuration={1500}
        disableDotsControls
        responsive={responsive}
        disableButtonsControls
        autoPlay
        items={items}
      />
    </ContainerCorousel>
  );
}

export default Carousel;

const ContainerCorousel = styled.div`
  height: 50%;
  display: flex;
  align-items: center;
  margin-top: 40px;
`;
