import { useContext, useEffect, useState } from "react";
import { CryptoState } from "../../CryptoContext/CryptoContext";
import { CoinData } from "../../Config/api";
import axios from "axios";
import "./CoinList.css";
import {
  Container,
  LinearProgress,
  Tab,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  TextField,
} from "@mui/material";
import { useNavigate } from "react-router-dom";
import styled from "@emotion/styled";

const CoinList = () => {
  const { currency, symbol } = useContext(CryptoState);
  const [coinSummary, setCoinSummary] = useState([]);
  const [loading, setLoading] = useState(false);
  const [search, setSearch] = useState("");
  const navigate = useNavigate();
  const fetchCoinData = async (currency) => {
    const { data } = await new Promise((res, rej) => {
      setTimeout(() => {
        try {
          res(axios.get(CoinData(currency)));
        } catch (err) {
          console.log(err);
        }
      }, 10);
    });
    setCoinSummary(data);
  };
  const handelSearch = () => {
    return coinSummary.filter(
      (coin) =>
        coin.name.toLowerCase().includes(search) ||
        coin.symbol.toLowerCase.includes(search)
    );
  };
  console.log(coinSummary);
  useEffect(() => {
    setLoading(true);
    fetchCoinData(currency);
    setLoading(false);
  }, [currency]);
  return (
    <div>
      <Container className="coin_summary_contaner">
        <p
          style={{
            margin: "15px 0 10px 0px",
            fontFamily: "Montserrat",
            fontSize: "32px",
            fontWeight: "600",
          }}
        >
          Get detail of your coins with market cap
        </p>
        <TextField
          style={{ margin: "20px", width: "100%" }}
          varient="outlined"
          label="Search for you favourite coin.."
          onChange={(e) => {
            setSearch(e.target.value);
          }}
        />
        <TableContainer>
          {loading ? (
            <LinearProgress
              style={{ backgroundColor: "gold" }}
            ></LinearProgress>
          ) : (
            <Table>
              <TableHead style={{ backgroundColor: "#EEBC1D" }}>
                <TableRow>
                  {["Coin", "Price", "24Hrs-Change", "Market Cap"].map(
                    (element) => {
                      return (
                        <TableCell
                          style={{
                            color: "black",
                            fontWeight: "700",
                            fontFamily: "Montserrat",
                          }}
                          key={element}
                          align={element === "coin" ? "" : "right"}
                        >
                          {element}
                        </TableCell>
                      );
                    }
                  )}
                </TableRow>
              </TableHead>
              <TableBody>
                {handelSearch().map((row) => (
                  <TableRow
                    key={row.id}
                    onClick={() => {
                      navigate(`coin/${row?.id}`);
                    }}
                  >
                    <TableCell
                      component="th"
                      scope="row"
                      style={{ display: "flex", gap: "15",flexDirection:"column",alignItems:"center",justifyContent:"center" }}
                    >
                      <img
                        src={row?.image}
                        alt={row?.name}
                        style={{ height: "20px" }}
                      />
                      <CoinName >
                        <span style={{ textTransform: "uppercase" }}>
                          {row?.symbol}
                        </span>
                        <span>{row?.name}</span>
                      </CoinName>
                    </TableCell>
                    <TableCell>
                      <CoinName>
                        <span>{symbol}
                        <span>{row?.current_price}</span>
                        </span>
                      </CoinName>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          )}
        </TableContainer>
      </Container>
    </div>
  );
};
export default CoinList;

const CoinName = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  color: #ffffff;
  margin:10px;
  font-size:12px;
`;
