import "./Detail.css";
import { useContext, useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import { SingleCoin } from "../../Config/api";
import { LinearProgress } from "@mui/material";
import CoinChart from "../../Components/CoinChart/CoinChart";
import CoinDetails from "../../Components/CoinDetails/CoinDetails";
import { CryptoState } from "../../CryptoContext/CryptoContext";
import { HistoricalChart } from "../../Config/api";

const Detail = () => {
  const [coinData, setCoinData] = useState();
  const [isLoading, setIsLoading] = useState(false);
  const { id } = useParams();
  const { currency, symbol } = useContext(CryptoState);
  const [coinChart,setCoinChart] = useState();
  const [day,setDay] = useState(365)
  
  const fetchCoinDetails = async (id) => {
    const { data } = await new Promise((res) => {
      res(axios.get(SingleCoin(id)));
    });
    setCoinData(data);
  };

  const fetchCoinChart = async()=>{
    try{
      const {data} = await new Promise(res=>{
        setTimeout(()=>{
          res(axios.get(HistoricalChart(id,day,currency)))
        },10)
      });
      setCoinChart(data)
    }
    catch(err){
      console.error(err)
    }
  }
  console.log(coinData);
  console.log(coinChart)
  useEffect(() => {
    setIsLoading(true);
    fetchCoinDetails(id);
    fetchCoinChart();
    setIsLoading(false);
  }, []);

  return (
    <div className="container">
    <h1 className="current_price">Current price: {symbol} {coinData?.market_data?.current_price?.usd}</h1> 
      {isLoading ? (
        <LinearProgress></LinearProgress>
      ) : (
        <div className="main_container">
          <CoinDetails coinData={coinData} />
          <CoinChart />
        </div>
      )}
    </div>
  );
};
export default Detail;
