import { CircularProgress } from "@mui/material";
import "./CoinChart.css";
import { useContext, useEffect, useState } from "react";
import styled from "styled-components";
import { Line } from "react-chartjs-2";
import { HistoricalChart } from "../../Config/api";
import { CryptoState } from "../../CryptoContext/CryptoContext";
import axios from "axios";
import { useParams } from "react-router-dom";

const CoinChart = ({coinData}) => {
  const [isLoading, setIsLoading] = useState(false);
  const { id } = useParams();
  const { currency, symbol } = useContext(CryptoState);
  const [coinChart, setCoinChart] = useState();
  const [day, setDay] = useState(1);

  const fetchCoinChart = async () => {
    try {
      const { data } = await new Promise((res) => {
        setTimeout(() => {
          res(axios.get(HistoricalChart(coinData.id, day, currency)));
        }, 1);
      });
      setCoinChart(data);
    } catch (err) {
      console.error(err);
    }
  };
  console.log(coinChart.prices);

  useEffect(() => {
    setIsLoading(true);
    fetchCoinChart();
    setIsLoading(false);
  }, [currency, day]);

  return (
    <div className="coin_chart">
      {isLoading ? (
        CoinChart(
          <CircularProgress
            style={{ Color: "gold" }}
            size={250}
            thickness={1}
          ></CircularProgress>
        )
      ) : (
        <>
          {/* <Line
           data={{
              labels: coinChart.prices.map((coin) => {
                let date = new Date(coin[0]);
                let time =
                  date.getHours() > 12
                    ? `${date.getHours() - 12}:${date.getMinutes()} PM`
                    : `${date.getHours()}:${date.getMinutes()} AM`;
              }),
            }}
          /> */}
        </>
      )}
    </div>
  );
};
export default CoinChart;
const Chart = styled.div``;
