import {useHistory} from 'react-router-dom'
const history = useHistory();
const Header=()=>{
    return (
        <NavBar>
        <Logo>
            Crypto Tr@cker
        </Logo>

        </NavBar>
    )
}
export default Header;
const NavBar = styled.div`
color:transparent;
position:sticky;
top:0;
width:100%;
display:flex;
flex-direction:row;
justify-content:space-between;
align-items:center;
padding: 20px;
`
const Logo = styled.div`
color:gold;
font-size:24px;
font-family:Montserrat;
cursor:pointer;
font-weight:bold;
`
